parameters.jsCode:
const data = items[0].json;

function formatCurrency(amount) {
  return new Intl.NumberFormat('en-IN', {
    style: 'currency',
    currency: 'INR',
    minimumFractionDigits: 0
  }).format(amount || 0);
}

function formatDate(dateString) {
  try {
    if (!dateString) return new Date().toLocaleDateString('en-IN');
    return new Date(dateString).toLocaleDateString('en-IN', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  } catch {
    return new Date().toLocaleDateString('en-IN');
  }
}

const customerEmail = data.customerEmail || data.email || data.shippingAddress?.email || data.user?.email;
const customerName = data.customerName || data.firstName || data.shippingAddress?.firstName || data.user?.firstName || 'Valued Customer';
const customerLastName = data.lastName || data.shippingAddress?.lastName || data.user?.lastName || '';
const customerFullName = `${customerName} ${customerLastName}`.trim();
const customerPhone = data.phone || data.shippingAddress?.phone || data.user?.phone || 'Not provided';

let itemsTableHtml = `
<table style="width: 100%; border-collapse: collapse; margin: 15px 0; border-radius: 8px; overflow: hidden; box-shadow: 0 2px 8px rgba(0,0,0,0.1);">
  <thead>
    <tr style="background: linear-gradient(135deg, #DB4444 0%, #c73e3e 100%); color: white;">
      <th style="padding: 15px; text-align: left; font-weight: 600;">Product</th>
      <th style="padding: 15px; text-align: center; font-weight: 600;">Size</th>
      <th style="padding: 15px; text-align: center; font-weight: 600;">Quantity</th>
      <th style="padding: 15px; text-align: center; font-weight: 600;">Price</th>
    </tr>
  </thead>
  <tbody>
`;

let itemsList = '';
let totalItems = 0;

if (data.items || data.itemDetails || data.products) {
  const items = data.items || data.itemDetails || data.products;

  if (Array.isArray(items)) {
    items.forEach((item, index) => {
      const bgColor = index % 2 === 0 ? '#ffffff' : '#f8f9fa';
      const productName = item.name || item.productId || item.product || item.title || `Product ${index + 1}`;
      const size = item.size || item.variant || 'N/A';
      const quantity = item.quantity || item.qty || 1;
      const price = item.price || item.amount || 0;

      totalItems += quantity;

      itemsTableHtml += `
      <tr style="background-color: ${bgColor}; transition: background-color 0.2s;">
        <td style="padding: 15px; border-bottom: 1px solid #dee2e6; font-weight: 500;">${productName}</td>
        <td style="padding: 15px; border-bottom: 1px solid #dee2e6; text-align: center;">${size}</td>
        <td style="padding: 15px; border-bottom: 1px solid #dee2e6; text-align: center; font-weight: 600;">${quantity}</td>
        <td style="padding: 15px; border-bottom: 1px solid #dee2e6; text-align: center; font-weight: 600;">${formatCurrency(price)}</td>
      </tr>`;

      itemsList += `${productName} (${size}) x${quantity}`;
      if (index < items.length - 1) itemsList += ', ';
    });
  }
} else {
  itemsTableHtml += `
  <tr>
    <td colspan="4" style="padding: 20px; text-align: center; color: #6c757d; font-style: italic;">No item details available</td>
  </tr>`;
  itemsList = 'Items information not available';
  totalItems = data.totalItems || data.itemCount || 1;
}

itemsTableHtml += `</tbody></table>`;

const shippingAddress = data.shippingAddress || data.address || {};
const customerCity = shippingAddress.city || data.city || 'Not provided';
const shippingHtml = `
<div style="background: linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%); padding: 20px; border-radius: 12px; border-left: 5px solid #DB4444; margin: 20px 0;">
  <h4 style="margin: 0 0 15px 0; color: #495057; font-size: 18px;">üìç Shipping Address</h4>
  <div style="background-color: white; padding: 15px; border-radius: 8px; line-height: 1.8; color: #495057;">
    <strong style="color: #DB4444; font-size: 16px;">${customerFullName}</strong><br>
    ${shippingAddress.streetAddress || shippingAddress.address || shippingAddress.line1 || 'Address not provided'}<br>
    ${shippingAddress.apartment || shippingAddress.line2 ? (shippingAddress.apartment || shippingAddress.line2) + '<br>' : ''}
    ${customerCity} - ${shippingAddress.pin || shippingAddress.pincode || shippingAddress.postalCode || 'PIN'}<br>
    <strong>üìû ${customerPhone}</strong>
  </div>
</div>`;

let couponHtml = '';
if (data.couponCode || data.appliedCoupon || data.discountCode) {
  const couponCode = data.couponCode || data.appliedCoupon || data.discountCode;
  const discount = data.discount || data.couponDiscount || data.discountAmount || 0;
  couponHtml = `
  <div style="background: linear-gradient(135deg, #d1ecf1 0%, #bee5eb 100%); border: 2px solid #17a2b8; padding: 20px; border-radius: 12px; margin: 20px 0; text-align: center;">
    <h4 style="margin: 0 0 10px 0; color: #0c5460; font-size: 18px;">üéâ Congratulations! Discount Applied</h4>
    <p style="margin: 5px 0; color: #0c5460; font-size: 16px;"><strong>Coupon Code:</strong> ${couponCode}</p>
    <p style="margin: 5px 0; color: #0c5460; font-size: 16px;"><strong>You Saved:</strong> ${formatCurrency(discount)}</p>
  </div>`;
}

const orderDate = formatDate(data.createdAt || data.timestamp || data.orderDate || data.date);
const deliveryDate = formatDate(data.estimatedDelivery || data.deliveryDate || data.expectedDelivery);
const paymentId = data.paymentId || data.razorpayPaymentId || data.transactionId || data.payment?.id || 'Processing';

return {
  ...data,
  customerEmail,
  customerName,
  customerFullName,
  customerPhone,
  customerCity,
  paymentId,
  orderDate,
  deliveryDate,
  formattedAmount: formatCurrency(data.amount || data.total || data.totalAmount || 0),
  itemsTableHtml,
  couponHtml,
  shippingHtml,
  itemsList,
  itemCount: totalItems,
  hasDiscount: !!(data.couponCode || data.appliedCoupon || data.discountCode),
  processedAt: new Date().toISOString()
};

const data = items[0].json;

const logEntry = {
  orderId: data.orderId,
  customerEmail: data.customerEmail,
  customerName: data.customerFullName,
  orderAmount: data.formattedAmount,
  timestamp: new Date().toISOString(),
  emailsSent: {
    customer: true,
    admin: true
  },
  status: 'completed'
};

console.log('Email automation completed:', JSON.stringify(logEntry, null, 2));

return {
  ...data,
  emailLog: logEntry,
  message: 'Email automation completed successfully'
};

